import sys
import io
from PIL import Image
import base64
import time

from selenium import webdriver
import os
dir_path = os.path.dirname(os.path.realpath(__file__))

# base64image = sys.argv[1]
base64test = 'data:image/png;base64,'
driver = webdriver.Chrome(dir_path + "/chromedriver")

try:
    # Open the website
    driver.get('https://images.google.com/')

    # Find the "I Agree" button and click it
    i_agree_button_xpath = "/ html / body / div[2] / div[1] / div[3] / span / div / div / div[3] / button[2]"

    driver.find_element_by_xpath(i_agree_button_xpath).click()

    # Find cam button
    cam_button = driver.find_elements_by_xpath("//div[@aria-label=\"Search by image\" and @role=\"button\"]")[0]
    cam_button.click()

    time.sleep(1)

    # Find upload tab
    upload_tab = driver.find_elements_by_xpath("//*[contains(text(), 'Upload an image')]")[0]
    upload_tab.click()

    image_file = open("imgToSave.png", "wb")
    image_file.write(base64.b64decode(base64test.split(',')[1]))

    # Find image input
    upload_btn = driver.find_element_by_xpath('/html/body/div[2]/div[3]/div[2]/div/div[2]/form/div[2]/div[2]/input[@type="file"]')
    upload_btn.sendKeys(image_file)

except Exception as e:
    print(e)


